mapper.insert:
INSERT INTO nxAccommodation(
	region
	,hotelType
	,hotelName
	,hotelIntroduce
	,addressZip
	,address
	,adressDetail
	,lng
	,lat
	,checkin
	,checkout
	,delNy
	)
VALUES(
	#{region }
	,#{hotelType }
	,#{hotelName }
	,#{hotelIntroduce }
	,#{addressZip }
	,#{address }
	,#{adressDetail }
	,#{lng }
	,#{lat }
	,#{checkin }
	,#{checkout }
	,0
	)

	SELECT last_insert_id()

INSERT INTO nxPurchaseHistory(
	memberSeq
	,nxAccommodationSeq
	,nxRoomSeq
	,reservationStatus
	,checkInDate
	,checkOutDate
	,pay
	,delNy
	,personnel
	)
VALUES(
	#{memberSeq }
	,#{nxAccommodationSeq }
	,#{nxRoomSeq }
	,1
	,#{checkInDate }
	,#{checkOutDate }
	,#{pay }
	,0
	,#{personnel }
	)

	SELECT last_insert_id()

mapper.update:
UPDATE nxPurchaseHistory
SET
	reservationName = #{reservationName }
	,reservationPhoneNum = #{reservationPhoneNum }
	,reservationStatus = 2
	,paymentMethod = #{paymentMethod }
WHERE 
	nxPurchaseHistorySeq = #{nxPurchaseHistorySeq}

