mapper.sql:
FROM nxPost a
JOIN member b on a.memberSeq = b.memberSeq
where 1=1
	AND a.delNy = #{shDelNy}
	AND a.postType = #{shPostType}
	AND a.region = #{shRegion}

		AND a.title LIKE CONCAT('%',#{shValue},'%')
		AND a.addressTitle LIKE CONCAT('%',#{shValue},'%')
		AND memberName LIKE CONCAT('%',#{shValue},'%')
		AND memberNickName LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO nxPost (
		memberSeq
		,region
		,postType
		,addressTitle
		,title
		,content
		,roadAddress
		,jibunAddress
		,lng
		,lat
		,delNy
	)
	VALUES (
		#{memberSeq}
		,#{region}
		,#{postType}
		,#{addressTitle}
		,#{title}
		,#{content}
		,#{roadAddress}
		,#{jibunAddress}
		,#{lng}
		,#{lat}
		,0
	)

		SELECT last_insert_id()

mapper.update:
UPDATE nxPost
	SET
	region = #{region}
	,postType = #{postType}
	,title = #{title}
	,content = #{content}
	,addressTitle = #{addressTitle}
	,roadAddress = #{roadAddress}
	,jibunAddress = #{jibunAddress}
	,lng = #{lng}
	,lat = #{lat}
WHERE 1=1
	AND	nxPostSeq = #{nxPostSeq}

mapper.select:
select a.nxPostSeq ,a.memberSeq ,a.region ,a.postType ,a.title ,a.delNy ,a.regDateTime ,a.modDateTime ,b.name as memberName ,b.nickname as memberNickName FROM nxPost a JOIN member b on a.memberSeq = b.memberSeq WHERE 1=1 AND a.memberSeq=#{memberSeq} AND a.delNy = 0 AND a.postType = #{shPostType} AND a.region = #{shRegion}
AND a.title LIKE CONCAT('%',#{shValue},'%') AND a.addressTitle LIKE CONCAT('%',#{shValue},'%') AND memberNickName LIKE CONCAT('%',#{shValue},'%')
ORDER BY nxPostSeq DESC
